import React, { Component } from 'react';
import './App.css';


import {CardList} from './components/card-list/card-list-component';


class App extends Component {

  constructor(){
    super();
    this.state = {
      monsters: [],
      searchField: ''
    }
  }
// componentDidMount: when component is loaded, do thing.
  componentDidMount(){

    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response => response.json())
    .then(users => this.setState({monsters: users}));

  }

  render(){

    const { monsters, searchField } = this.state; //equivalent to const monsters = this.state.monsters; ect.

    const filteredMonsters = monsters.filter(monster => 
        monster.name.toLowerCase().includes(searchField.toLowerCase())
        )


    return (
      <div className="App">
        <input 
          type='search' 
          placeholder='search monsters' 
          onChange={ e => this.setState({searchField: e.target.value})} //if you want to see something right after you call set state, you 
        />
        <CardList monsters = {filteredMonsters} /> 
      </div>
    );
  }
}

export default App;
